operation.search:
$request = $smcFunc['db_query']('', '
	SELECT id_member, poster_name, poster_email, poster_time, approved
	FROM {db_prefix}messages
	WHERE id_msg = {int:id_msg}
	LIMIT 1',
	array(
		'id_msg' => $_REQUEST['msg'],
	)
);

operation.add:
$request = $smcFunc['db_query']('', '
	SELECT m.id_member, m.poster_name, m.poster_email, m.poster_time, m.approved, m.id_topic, m.subject, t.id_first_msg
	FROM {db_prefix}messages AS m
	INNER JOIN {db_prefix}topics AS t ON m.id_topic = t.id_topic
	WHERE m.id_msg = {int:id_msg}
	LIMIT 1',
	array(
		'id_msg' => $_REQUEST['msg'],
	)
);

// Should we change all subjects?
if (!empty($change_all_subjects))
{
	$request = $smcFunc['db_query']('', '
		SELECT id_msg
		FROM {db_prefix}messages
		WHERE id_topic = {int:id_topic}',
		array(
			'id_topic' => $change_all_subjects
		));

	$messages_to_change = array();
	while ($row = $smcFunc['db_fetch_row']($request))
	{
		if ($_REQUEST['msg'] != $row[0])
			$messages_to_change[] = $row[0];
	}

	$smcFunc['db_free_result']($request);

	// Now do the actual updating!
	if (!empty($messages_to_change))
		$smcFunc['db_query']('', '
			UPDATE {db_prefix}messages
			SET subject = {string:subject}
			WHERE id_msg IN ({array_int:msgs})',
			array(
				'subject' => 'Re: ' . $_POST['subject'],
				'msgs' => $messages_to_change
			));
}
